    public frmChitiethdnhaphang(String id) {
        initComponents();
        this.setLocationRelativeTo(null);
        ctbhDL = new CTHDNhapHangXLDL();
        SPDL = new SanPhamXLDL();
        uk = new UrlKN();
        dao = new DAO(uk.knURL());
        HDNHDL = new HDNhapHangXLDL();
        maHDNH = id;
        loadnv();
        loadmaNV();
        btnLuu.setEnabled(false);
        btnLuu.setBackground(Color.GRAY);
        txtMaHDNH.setText("" + maHDNH);
    }

    void loadnv() {

        ArrayList<Chitiethdnh> list = new ArrayList();
        list.clear();
        try {
            list = ctbhDL.getListCTNH(maHDNH);
            tablemodel = (DefaultTableModel) tblChitiethdnhaphang.getModel();
            for (Chitiethdnh ct : list) {
                tablemodel.addRow(new Object[]{ct.getMaHDNH(), ct.getTenSP(), ct.getDonGiaNhap(), ct.getSoLuong(), ct.getTongTien()});
            }
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog(this, ex.toString());
        }
    }

    void loadmaNV() {
        try {
            ArrayList<TblSanpham> SP = SPDL.getListSP();
            for (TblSanpham sp : SP) {
                cboMasanpham.addItem(sp);
            }
        } catch (SQLException ex) {

        }
    }

    void hienthi(int i) {
        TableModel model = tblChitiethdnhaphang.getModel();
        txtMaHDNH.setText(model.getValueAt(i, 0).toString());

        String cbosub12 = model.getValueAt(i, 1).toString();
        for (int j = 0; j < cboMasanpham.getItemCount(); j++) {
            if (cboMasanpham.getItemAt(j).toString().equalsIgnoreCase(cbosub12)) {
                cboMasanpham.setSelectedIndex(j);
            }
        }
        txtDongianhap.setText(model.getValueAt(i, 2).toString());
        txtSoluong.setText(model.getValueAt(i, 3).toString());
        txtTongtien.setText(model.getValueAt(i, 4).toString());
    }

    private boolean KTDL() throws HeadlessException {
        StringBuilder sb = new StringBuilder();

        if (txtSoluong.getText().equals("")) {
            sb.append("Vui lòng nhập số lượng\n");
            txtSoluong.requestFocus();
        }
        if (txtTongtien.getText().equals("")) {
            sb.append("Vui lòng nhập tổng tiền\n");
            txtTongtien.requestFocus();
        }
        if (txtDongianhap.getText().equals("")) {
            sb.append("Vui lòng nhập đơn giá nhập\n");
            txtDongianhap.requestFocus();
        }
        if (sb.length() > 0) {
            JOptionPane.showMessageDialog(this, sb.toString(), "Thông báo", JOptionPane.ERROR_MESSAGE);
            return false;
        } else {
            return true;
        }
    }

