    private void btnXoaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnXoaActionPerformed
        // TODO add your handling code here:
        int chon = JOptionPane.showConfirmDialog(this, "Bạn Có Muốn Xóa?", "xoa", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE);
        if (chon == JOptionPane.YES_NO_OPTION) {
            TblSanpham dm = (TblSanpham) cboMasanpham.getSelectedItem();
            String MaSP = String.valueOf(dm.getMaSP());
            Connection conn = dao.getConnect();
            String caulenh = "delete from chitiethdnh where MaHDNH=" + txtMaHDNH.getText() + " and MaSP=" + MaSP;
            boolean a = dao.Lenh(caulenh, conn);
            String capnhat = "UPDATE tbl_sanpham SET SLTon = SLTon- " + Integer.parseInt(txtSoluong.getText().toString()) + " WHERE MaSP=" + MaSP;
            boolean b = dao.Lenh(capnhat, conn);
            String capnhat1 = "UPDATE tbl_hoadonnhaphang SET  TongTien = TongTien- " + Float.parseFloat(txtTongtien.getText().toString()) + " WHERE MaHDNH=" + txtMaHDNH.getText().toString();
            boolean c = dao.Lenh(capnhat1, conn);
            if (a) {
                tblChitiethdnhaphang.removeAll();
                tablemodel.setRowCount(0);
                loadnv();
                JOptionPane.showMessageDialog(this, "Xóa Thành Công");
            } else {
                JOptionPane.showMessageDialog(this, "Xóa không thành công");
            }
        }
    }//GEN-LAST:event_btnXoaActionPerformed

    private void btnLuuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLuuActionPerformed
        // TODO add your handling code here:
        Color mauxanh = new Color(255, 153, 0);
        btnThem.setBackground(mauxanh);
        btnXoa.setEnabled(true);
        btnXoa.setBackground(mauxanh);
        btnLuu.setEnabled(false);
        btnLuu.setBackground(Color.GRAY);
        int chon = JOptionPane.showConfirmDialog(this, "Bạn Có Muốn Lưu?", "Lưu", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE);
        if (chon == JOptionPane.YES_NO_OPTION) {
            if (KTDL()) {
                if (them) {
                    TblSanpham dm = (TblSanpham) cboMasanpham.getSelectedItem();
                    String MaSP = String.valueOf(dm.getMaSP());
                    String HDNH = txtMaHDNH.getText();
                    try {
                        Connection conn = dao.getConnect();
                        String caulenh = "INSERT INTO chitiethdnh VALUE('" + HDNH + "','" + MaSP + "','" + txtDongianhap.getText() + "'," + txtSoluong.getText() + ",'" + txtTongtien.getText() + "')";
                        boolean a = dao.Lenh(caulenh, conn);
                        String capnhat = "UPDATE tbl_sanpham SET SLTon = SLTon+ " + Integer.parseInt(txtSoluong.getText().toString()) + " WHERE MaSP=" + MaSP;
                        boolean b = dao.Lenh(capnhat, conn);
                        String capnhat1 = "UPDATE tbl_hoadonnhaphang SET  TongTien = TongTien+ " + Float.parseFloat(txtTongtien.getText().toString()) + " WHERE MaHDNH=" + txtMaHDNH.getText().toString();
                        boolean c = dao.Lenh(capnhat1, conn);
                        if (a) {
                            tblChitiethdnhaphang.removeAll();
                            tablemodel.setRowCount(0);
                            loadnv();
                            JOptionPane.showMessageDialog(this, "Thêm Thành Công");
                        } else {
                            JOptionPane.showMessageDialog(this, "Thêm không thành công");
                        }
                    } catch (Exception ex) {
                        Logger.getLogger(frmChitiethdnhaphang.class.getName()).log(Level.SEVERE, null, ex);
                    }
                    txtDongianhap.setText("");
                    txtSoluong.setText("");
                    txtTongtien.setText("");
                }
                if (sua) {
                    TblSanpham dm = (TblSanpham) cboMasanpham.getSelectedItem();
                    String MaSP = String.valueOf(dm.getMaSP());
                    String HDNH = txtMaHDNH.getText();
                    try {
                        Connection conn = dao.getConnect();
                        String caulenh = "update chitiethdnh set DonGiaNhap='" + txtDongianhap.getText() + "', SoLuong='" + txtSoluong.getText() + "', TongTien='" + txtTongtien.getText() + "' where MaHDNH=" + HDNH + " and MaSP=" + MaSP;
                        boolean a = dao.Lenh(caulenh, conn);
                        String capnhat = "UPDATE tbl_sanpham SET SLTon = SLTon " + Integer.parseInt(txtSoluong.getText().toString()) + " WHERE MaSP=" + MaSP;
                        boolean b = dao.Lenh(capnhat, conn);
                        String capnhat1 = "UPDATE tbl_hoadonnhaphang SET  TongTien = TongTien " + Float.parseFloat(txtTongtien.getText().toString()) + " WHERE MaHDNH=" + txtMaHDNH.getText().toString();
                        boolean c = dao.Lenh(capnhat1, conn);
                        if (a) {
                            tblChitiethdnhaphang.removeAll();
                            tablemodel.setRowCount(0);
                            loadnv();
                            JOptionPane.showMessageDialog(this, "sửa Thành Công");
                        } else {
                            JOptionPane.showMessageDialog(this, "sửa không thành công");
                        }
                    } catch (Exception ex) {
                        Logger.getLogger(frmChitiethdnhaphang.class.getName()).log(Level.SEVERE, null, ex);
                    }
                }
            }
    }//GEN-LAST:event_btnLuuActionPerformed
    }
    private void lbltikmkiem1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_lbltikmkiem1ActionPerformed
        // TODO add your handling code here:
        tblChitiethdnhaphang.removeAll();
        tablemodel.setRowCount(0);
        String tk = txtTimkiem.getText();
        ArrayList<Chitiethdnh> list = new ArrayList();
        list.clear();
        try {
            list = ctbhDL.timkiemCTNH(tk);
            tablemodel = (DefaultTableModel) tblChitiethdnhaphang.getModel();
            for (Chitiethdnh ct : list) {
                tablemodel.addRow(new Object[]{ct.getMaHDNH(), ct.getTenSP(), ct.getDonGiaNhap(), ct.getSoLuong(), ct.getTongTien()});
            }
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog(this, ex.toString());
        }
    }//GEN-LAST:event_lbltikmkiem1ActionPerformed

