    void tongtien() {
        int soluong = Integer.parseInt(txtSoluong.getText());
        float donGia = Float.parseFloat(txtDongianhap.getText());
        float tongTien = soluong * donGia;
        txtTongtien.setText("" + tongTien);

    }
