    private void btnSignMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnSignMouseClicked
        // TODO add your handling code here:
        String taikhoan = txttendangnhap.getText().toString();
        String matkhau = txtMatkhau.getText().toString();
        
        try {
            String tt = DN.dangnhap(taikhoan, matkhau);
            if (!tt.equals("0")) {
               frmHome hm = new frmHome(tt);
                hm.setVisible(true);
            }else{
                JOptionPane.showMessageDialog(this, "Tài khoản hoặc mật khẩu không chính xác");
            }
        } catch (SQLException ex) {
            Logger.getLogger(frmDangnhap.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btnSignMouseClicked

