    void loadnv() {

        ArrayList<TblDanhmuc> list = new ArrayList();
        list.clear();
        try {
            list = nvDL.getListDM();
            tablemodel = (DefaultTableModel) tblDanhmuc.getModel();
            for (TblDanhmuc ct : list) {
                tablemodel.addRow(new Object[]{ct.getMaDM(), ct.getTenDM()});
            }
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog(this, ex.toString());
        }
    }

    private boolean KTDL() throws HeadlessException {
        StringBuilder sb = new StringBuilder();

        if (txtTendanhmuc.getText().equals("")) {
            sb.append("Vui lòng nhập tên danh mục\n");
            txtTendanhmuc.requestFocus();
        }

        if (sb.length() > 0) {
            JOptionPane.showMessageDialog(this, sb.toString(), "Thông báo", JOptionPane.ERROR_MESSAGE);
            return false;
        } else {
            return true;
        }
    }

