    void loadnv(String NgayNhap) {

        ArrayList<TblHoadonnhaphang> list = new ArrayList();
        list.clear();
        try {
            list = nvDL.getListHDNH(NgayNhap);
            tablemodel = (DefaultTableModel) tblHoadonnhaphang.getModel();
            for (TblHoadonnhaphang nv : list) {
                tablemodel.addRow(new Object[]{nv.getMaHDNH(), nv.getTenNV(), nv.getNgayNhap(), nv.getTongTien()});
            }
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog(this, ex.toString());
        }

    }
    private boolean KTDL() throws HeadlessException{
        StringBuilder sb = new StringBuilder();
        if (txtMahdnhaphang.getText().equals("")) {
            sb.append("Vui lòng chọn hóa đơn trong bảng");
            txtMahdnhaphang.requestFocus();
        }
         if (sb.length() > 0) {
            JOptionPane.showMessageDialog(this, sb.toString(), "Thông báo", JOptionPane.ERROR_MESSAGE);
            return false;
         }else{
            return true;
        }
    }
    
    void loadmaNV() {
        try {
            ArrayList<TblNhanvien> l = NDL.getListNV();
            for (TblNhanvien nv : l) {
                cboManhanvien.addItem(nv);
            }
        } catch (SQLException ex) {

        }
    }

