    void hienthi(int i) {
        TableModel model = tblHoadonnhaphang.getModel();
        txtMahdnhaphang.setText(model.getValueAt(i, 0).toString());
        String cbosub = model.getValueAt(i, 1).toString();
        for (int j = 0; j < cboManhanvien.getItemCount(); j++) {
            if (cboManhanvien.getItemAt(j).toString().equalsIgnoreCase(cbosub)) {
                cboManhanvien.setSelectedIndex(j);
            }
        }
        Date date = null;
        try {
            date = new SimpleDateFormat("yyyy-MM-dd").parse(model.getValueAt(i, 2).toString());

        } catch (ParseException ex) {
            Logger.getLogger(frmHoadonnhaphang.class
                    .getName()).log(Level.SEVERE, null, ex);
        }
        DateNgayNhap.setDate(date);
        txtTongtien.setText(model.getValueAt(i, 3).toString());

    }
