    private void btnLuuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLuuActionPerformed
        // TODO add your handling code here:
        Color mauxanh = new Color(255, 153, 0);
        btnThem.setBackground(mauxanh);
        btnSua.setEnabled(true);
        btnSua.setBackground(mauxanh);
        btnXoa.setEnabled(true);
        btnXoa.setBackground(mauxanh);
        btnLuu.setEnabled(false);
        btnLuu.setBackground(Color.GRAY);
        int chon = JOptionPane.showConfirmDialog(this, "Bạn Có Muốn Lưu?", "Lưu", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE);
        if (chon == JOptionPane.YES_NO_OPTION) {
            if (them) {
                Date dt = new Date();
                SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd");
                String ngay = dateFormat.format(dt);
                TblNhanvien dmm = (TblNhanvien) cboManhanvien.getSelectedItem();
                String MaNV = String.valueOf(dmm.getMaNV());
               
           
                try {
                    Connection conn = dao.getConnect();
                    String caulenh = "INSERT INTO tbl_hoadonnhaphang VALUE(Null,'" + MaNV + "','" + ngay + "','" + 0 + "')";
                    boolean a = dao.Lenh(caulenh, conn);
                    if (a) {
                        tblHoadonnhaphang.removeAll();
                        tablemodel.setRowCount(0);
                        loadnv(ngay);
                        JOptionPane.showMessageDialog(this, "Thêm Thành Công");
                    } else {
                        JOptionPane.showMessageDialog(this, "Thêm không thành công");

                    }
                } catch (Exception ex) {
                    Logger.getLogger(frmHoadonnhaphang.class
                            .getName()).log(Level.SEVERE, null, ex);
                }
            }
            if (sua) {
                Date dt = new Date();
                SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd");
                String ngay = dateFormat.format(dt);
                TblNhanvien dmm = (TblNhanvien) cboManhanvien.getSelectedItem();
                String MaNV = String.valueOf(dmm.getMaNV());
                try {
                    Connection conn = dao.getConnect();
                    String caulenh = "update tbl_hoadonnhaphang set MaNV='" + MaNV + "',ngaynhap='" + ngay + "', TongTien='" + txtTongtien.getText() + "' where MaHDNH=" + txtMahdnhaphang.getText();
                    boolean a = dao.Lenh(caulenh, conn);
                    if (a) {
                        tblHoadonnhaphang.removeAll();
                        tablemodel.setRowCount(0);
                        loadnv(ngay);
                        JOptionPane.showMessageDialog(this, "sửa Thành Công");
                    } else {
                        JOptionPane.showMessageDialog(this, "sửa không thành công");

                    }
                } catch (Exception ex) {
                    Logger.getLogger(frmHoadonnhaphang.class
                            .getName()).log(Level.SEVERE, null, ex);
                }
            }
        }
    }//GEN-LAST:event_btnLuuActionPerformed

    private void lbltikmkiemActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_lbltikmkiemActionPerformed
        // TODO add your handling code here:
        tblHoadonnhaphang.removeAll();
        tablemodel.setRowCount(0);
        String tk = txtTimkiem.getText();
        ArrayList<TblHoadonnhaphang> list = new ArrayList();
        list.clear();
        try {
            list = nvDL.timkiemHDNH(tk);
            tablemodel = (DefaultTableModel) tblHoadonnhaphang.getModel();
            for (TblHoadonnhaphang nv : list) {
                tablemodel.addRow(new Object[]{nv.getMaHDNH(), nv.getTenNV(), nv.getNgayNhap(), nv.getTongTien()});
            }
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog(this, ex.toString());
        }
    }//GEN-LAST:event_lbltikmkiemActionPerformed

    private void btnNextActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNextActionPerformed
        // TODO add your handling code here:
        SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd", Locale.getDefault());
        Date tomorrow = new Date(today.getTime() + (1000 * 60 * 60 * 24));
        today = tomorrow;
        String ngay = dateFormat.format(tomorrow);
        try {
            tblHoadonnhaphang.removeAll();
            tablemodel.setRowCount(0);
            DateNgayNhap.setDate(today);
            loadnv(ngay);
        } catch (Exception e) {

            loadnv(ngay);
        }
    }//GEN-LAST:event_btnNextActionPerformed

    private void btnCenterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCenterActionPerformed
        // TODO add your handling code here:
        SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd", Locale.getDefault());
        try {
            tblHoadonnhaphang.removeAll();
            tablemodel.setRowCount(0);
            today = new Date();
            DateNgayNhap.setDate(today);
            String ngay = dateFormat.format(today);
            loadnv(ngay);
        } catch (Exception e) {
            today = new Date();
            String ngay = dateFormat.format(today);
            loadnv(ngay);
        }
    }//GEN-LAST:event_btnCenterActionPerformed

    private void btnBackActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBackActionPerformed
        // TODO add your handling code here:
        SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd", Locale.getDefault());
        Date tomorrow = new Date(today.getTime() - (1000 * 60 * 60 * 24));
        today = tomorrow;
        String ngay = dateFormat.format(tomorrow);
        try {
            tblHoadonnhaphang.removeAll();
            tablemodel.setRowCount(0);
            DateNgayNhap.setDate(today);
            loadnv(ngay);
        } catch (Exception e) {

            loadnv(ngay);
        }
    }//GEN-LAST:event_btnBackActionPerformed


