    public frmHome(String quyen) {
        initComponents();
        this.setLocationRelativeTo(null);
        setExtendedState(JFrame.MAXIMIZED_BOTH);
        Quyen = quyen;
        if (quyen.equals("Quản Lý")) {
            
        } else {            
            btnDanhmuc.setEnabled(false);
            btnSanPham.setEnabled(false);
            btnLuongnv.setEnabled(false);
            btnNhanvien.setEnabled(false);
            btnThongKe.setEnabled(false);
        }
        this.hienthi();
        ClockThread th = new ClockThread(btnClock);
        th.start();
        btnClock.setEnabled(true);
    }
    static String Quyen;
    
    void hienthi() {
        frmHome1 mn = new frmHome1();
        jDesktopPane1.removeAll();
        jDesktopPane1.add(mn).setVisible(true);
    }

